# Given an empty string
# It responds with "No text present"

def test_empty_text():
    output = reading_time("")
    expected = "No text present"
    assert output == expected


# Given a list of 200 words
# It returns a calculated reading time of 1.0 minutes

def test_return_time_for_200_word_string():
    output = reading_time(words_200)
    expected = "Your text will take 1.0 minute/s to read"
    assert output == expected

# Given a list of 400 words
# It returns a calculated reading time of 2.0 minutes

def test_return_time_for_400_word_string():
    output = reading_time(words_400)
    expected = "Your text will take 2.0 minute/s to read"
    assert output == expected

# Given a list of 400 words
# It returns a calculated reading time of 2.0 minutes

def test_return_time_for_100_word_string():
    output = reading_time(words_100)
    expected = "Your text will take 0.5 minute/s to read"
